import axios from "axios";
import { useEffect, useState } from "react";
import "../TypeColor.css"

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([]);
    const [nombres, setNombres] = useState([]);

    async function obtenerDatos() {
        try {
            const res = await axios.get(
            "https://pokeapi.co/api/v2/pokemon/?offset=0&limit=183"
            );
        setPokemon(res.data.results);
        } catch (err) {
            console.log(`Ocurrio un error: ${err}`);
        }
    }

    useEffect(() => {
        async function obtenerNombres() {
            const results = await Promise.all(
                pokemon.map(async (pokemon) => {
                    try {
                        const res = await axios.get(
                        `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
                        );
                    return res.data;
                    } catch (err) {
                        console.log(`Ocurrio un error: ${err}`);
                    }
                })
            );
            setNombres(results);
        }
        obtenerDatos();
        obtenerNombres();
    }, [pokemon]);

    const estilos = {
        main: "flex m-10 py-1 bg-[#b13241e6]",
        // Boton: "h-[800px] w-[55px] mx-4 bg-[#1b6102] border-[10px] rounded-lg border-[#2da202] border-double overflow-hidden self-center",
        Grid: "grid grid-cols-3 justify-stretch m-5",
    }

    return (
        <main className={estilos.main}>
            <div className={estilos.Grid}>
            {/* función de mapeo del grid */}
            {nombres.slice(0, 183).map((element) => (
                <div id={`${element.types[0].type.name}`} key={element.id}>
                    {/* texto de número y nombre */}
                    <p>{element.id.toString().padStart(4, '0')}.<br></br> {element.name}</p>
                    {/* sprite */}
                    <img src={element.sprites.front_default} alt="SPRITE" />
                    {/* div de tipos */}
                    <div className="flex flex-col my-2">
                        {/* función de mapeo de tipos */}
                        {element.types.map((type, index) => (
                            <p className={`${type.type.name}`} key={index}>
                            {`${type.type.name}`}
                            </p>
                        ))}
                    </div>
                </div>
            ))}
            </div>
        </main>
        );

};

export default ListaPokeDex;
