import axios from "axios";
import { useEffect, useState } from "react";
import "../TypeColor.css"

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([]);
    const [nombres, setNombres] = useState([]);

    async function obtenerDatos() {
        try {
            const res = await axios.get(
            "https://pokeapi.co/api/v2/pokemon/?offset=0&limit=21"
            );
        setPokemon(res.data.results);
        } catch (err) {
            console.log(`Ocurrio un error: ${err}`);
        }
    }

    useEffect(() => {
        async function obtenerNombres() {
            const results = await Promise.all(
                pokemon.map(async (pokemon) => {
                    try {
                        const res = await axios.get(
                        `https://pokeapi.co/api/v2/pokemon/${pokemon.name}`
                        );
                    return res.data;
                    } catch (err) {
                        console.log(`Ocurrio un error: ${err}`);
                    }
                })
            );
            setNombres(results);
        }
        obtenerDatos();
        obtenerNombres();
    }, [pokemon]);

    const estilos = {
        Main: "h-full w-full flex justify-center content-center mt-3 overflow-hidden",
        Boton: "h-[800px] w-[55px] mx-4 bg-[#1b6102] border-[10px] rounded-lg border-[#2da202] border-double overflow-hidden self-center",
        Grid: "w-full grid grid-cols-7 bg-[#cf3d4e]",
    }

    return (
        <main className={estilos.Main}>
            <button className={estilos.Boton}>
                <p className="rotate-[-90deg]">PREVIOUS</p>
            </button>
            <div className={estilos.Grid}>
                {nombres.slice(0, 21).map((element) => (
                <div className="flex flex-col m-1 border-2 border-black bg-[#d05462] " key={element.id}>
                    <p>{element.id.toString().padStart(4, '0')}. </p>
                    <p> {element.name}</p>
                    <img src={element.sprites.front_default} alt="SPRITE" />
                    <div>
                    {element.types.map((type) => (
                        <p className={`${type.type.name}`} key={type.slot}>{`${type.type.name}`}</p>
                        ))}
                    </div>
                </div>
                ))}
            </div>
            <button className={estilos.Boton}>
                <p className="rotate-[90deg]">NEXT</p>
            </button>
        </main>
    );
};

export default ListaPokeDex;
