import axios from "axios";
import { useEffect, useState } from "react";

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([])
    // const [SpriteUrl, setSpriteUrl] = useState([])
    let [nombres, setNombres] = useState([])

    async function obtenerDatos() {
        try {
            const res = await axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=1281');
            setPokemon(res.data.results);
        } catch (err) {
            console.log(`Ocurrio un error: ${err}`);
            }
        }

    useEffect(() => {
        pokemon.map(
            async ()=> {
                try {
                    const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon.name}`)
                    setNombres(nombres.push(res.data.sprites.front_default))
                } catch (err) {
                    console.log(`Ocurrio un error: ${err}`);
                }
            }
        )

        obtenerDatos();
        // obtenerSpriteUrl();
    },[pokemon,nombres]);

    return ( 
        <main>
            {nombres.map(
                (element) => (
                    <img src={element} alt="SPRITE" />
                )
            )}
            {pokemon.map(
                (element) => (
                    <p key={element.name}>{element.name}
                    </p>
                )
            )}
        </main>
    );
}

export default ListaPokeDex;
