import axios from "axios";
import { useEffect, useState } from "react";

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([])
    // const [SpriteUrl, setSpriteUrl] = useState([])
    let [nombres, setNombres] = useState([])

    async function obtenerDatos() {
        try {
            const res = await axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=1281');
            setPokemon(res.data.results);
        } catch (err) {
            console.log(`Ocurrio un error: ${err}`);
            }
        }

    useEffect(() => {
        pokemon.map(
            async (pokemon)=> {
                try {
                    const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon.name}`)
                    setNombres(nombres => [...nombres, res.data.sprites.front_default])
                    console.log(nombres)
                } catch (err) {
                    console.log(`Ocurrio un error: ${err}`);
                }
            }
        )

        obtenerDatos();
        // obtenerSpriteUrl();
    },[pokemon,nombres]);

    let id = 1;

    return ( 
        <main>
            {nombres.map(
                (element) => (
                    <div key={id++}>
                        <p>{element.name}</p>
                        <img src={element} alt="SPRITE" />
                    </div>

                )
            )}
        </main>
    );
}

export default ListaPokeDex;
