import axios from "axios";
import { useEffect, useState } from "react";

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([])
    const [SpriteUrl, setSpriteUrl] = useState([])

    useEffect(() => {
        async function obtenerDatos() {
            try {
                const res = await axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=1281');
                setPokemon(res.data.results);
            } catch (err) {
                console.log(`Ocurrio un error: ${err}`);
                }
            }
        // async function obtenerSpriteUrl() {
        //     try {
        //         const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon.results.name}`)
        //         setSpriteUrl(res.data.sprites.front_default);
        //     } catch (err) {
        //         console.log(`Ocurrio un error: ${err}`);
        //     }
        // }    
        obtenerDatos();
        // obtenerSpriteUrl();
    },[SpriteUrl,pokemon]);

    return ( 
        <main>
            {SpriteUrl.map(
                (element) => (
                    <img src={element.SpriteUrl} alt="SPRITE" />
                )
            )}
            {pokemon.map(
                (element) => (
                    <p key={element.name}>{element.name}
                    </p>
                )
            )}
        </main>
    );
}

export default ListaPokeDex;
