import axios from "axios";
import { useEffect, useState } from "react";

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([])
    const [SpriteUrl, setSpriteUrl] = useState([])
    async function obtenerDatos() {
        try {
            const res = await axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=100000');
            setPokemon(res.data.results);
        } catch (err) {
            console.log(`Ocurrio un error: ${err}`);
            // if(err.response.status === 404){
            //     // DETERMINAR
            //     }
            }
        }

    async function obtenerSpriteUrl() {
        try {
            const res = await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemon}`)
            setSpriteUrl(res.data.sprites.front_default);
        } catch (err) {
            console.log(`Ocurrio un error: ${err}`);
        }
    }

    useEffect(() => {
        obtenerDatos();
        obtenerSpriteUrl();
    },);
    return ( 
        <main>
            {SpriteUrl.map(
                (element) => (
                    <img src={setSpriteUrl} alt="SPRITE" />
                )
            )}
            {pokemon.map(
                (element) => (
                    <p key={element.name}>{element.name}
                    </p>
                )
            )}
        </main>
    );
}

export default ListaPokeDex;
