import axios from "axios";
import { useEffect, useState } from "react";

const ListaPokeDex = () => {
    const [pokemon, setPokemon] = useState([])
    const [sprite, setSprite] = useState([])
    async function obtenerDatos() {
        try {
            const res = await axios.get('https://pokeapi.co/api/v2/pokemon/?offset=0&limit=100000');
            setPokemon(res.data.results);
        } catch (err) {
            console.log(err)
            if(err.response.status === 404){
                alert('No se encontro la ruta')
            }
            console.log(`Ocurrio un error: ${err}`);
        }
    }
    async function obtenerSprite() {
        try {
            const res = await axios.get('https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/'+{sprite}+'.png')
            setSprite(res.data.results);
        } catch (err) {
            console.log(err)
            if(err.response.status === 404){
                alert('No se encontro la ruta')
            }
            console.log(`Ocurrio un error: ${err}`);
        }
    }

    useEffect(() => {
        obtenerDatos();
    });
    useEffect(() => {
        obtenerSprite();
    });
    return ( 
        <main>
            {sprite.map(
                (element) => (
                    <img src={element.sprite} alt="" />
                )
            )}
            {pokemon.map(
                (element) => (
                    <p>{element.name}</p>
                )
            )}
        </main>
    );
}

export default ListaPokeDex;
